mapper.update:
update gradereply
set content = #{content}
where grno = #{grno}

mapper.select:
select grno, content, to_char(rdate,'yyyy-mm-dd') rdate, id,
gno, r
FROM(
	select grno, content, rdate, id, gno, rownum r
	FROM(
		select grno, content, rdate, id, gno
		from gradereply
		where gno = #{gno}
		order by grno DESC
		)
	)		
	
	WHERE r >= #{sno} and r <= #{eno}
